CREATE TABLE fandom
(
    id             INTEGER AUTO_INCREMENT PRIMARY KEY,
    name           VARCHAR(100) UNIQUE,
    createdAt      TIMESTAMP,
    updatedAt      TIMESTAMP
);

CREATE TABLE genre
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    name             VARCHAR(100) UNIQUE,
    createdAt      TIMESTAMP,
    updatedAt      TIMESTAMP
);

CREATE TABLE category
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    name             VARCHAR(100) UNIQUE,
    createdAt      TIMESTAMP,
    updatedAt      TIMESTAMP
);

CREATE TABLE tag
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    name             VARCHAR(100) UNIQUE,
    createdAt      TIMESTAMP,
    updatedAt      TIMESTAMP
);

CREATE TABLE user
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    name             VARCHAR(100) UNIQUE,
    password        VARCHAR(100),
    email        VARCHAR(100) UNIQUE,
    createdAt      TIMESTAMP,
    updatedAt      TIMESTAMP,
    age            INT,
    about           VARCHAR(10000),
    contactInfo           VARCHAR(10000),
    admin          BIT,
    blocked         BIT
);

CREATE TABLE book
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    name             VARCHAR(100) UNIQUE,
    description      VARCHAR(5000),
    likes INT,
    rating INT,
    createdAt      TIMESTAMP,
    updatedAt      TIMESTAMP,
    fandom INTEGER REFERENCES fandom (id) ON DELETE CASCADE,
    category INTEGER REFERENCES category (id) ON DELETE CASCADE,
    genre INTEGER REFERENCES genre (id) ON DELETE CASCADE,
    user INTEGER REFERENCES user (id) ON DELETE CASCADE
);

CREATE TABLE chapter
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    name             VARCHAR(100) UNIQUE,
    text      VARCHAR(500000),
    number INT,
    createdAt      TIMESTAMP,
    updatedAt      TIMESTAMP,
    book INTEGER REFERENCES book (id) ON DELETE CASCADE
);

CREATE TABLE book_tag_map
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    book INTEGER REFERENCES book (id) ON DELETE CASCADE,
    tag INTEGER REFERENCES tag (id) ON DELETE CASCADE,
    updatedAt      TIMESTAMP,
    createdAt      TIMESTAMP
);

CREATE TABLE comment
(
    id               INTEGER AUTO_INCREMENT PRIMARY KEY,
    text      VARCHAR(500),
    book INTEGER REFERENCES book (id) ON DELETE CASCADE,
    user INTEGER REFERENCES user (id) ON DELETE CASCADE,
    updatedAt      TIMESTAMP,
    createdAt      TIMESTAMP
);

